mapper.select:
 	SELECT * FROM qna
	ORDER BY board_ref DESC, board_step ASC
	LIMIT #{endIndex} OFFSET #{startIndex}

mapper.update:

UPDATE QNA
SET board_ref = #{boardNo}
WHERE board_no = #{boardNo}

UPDATE qna
SET board_step = board_step + 1
WHERE board_ref = #{boardRef} AND board_step > #{boardStep}
