mapper.select:
 	SELECT * FROM qna

	ORDER BY board_ref DESC, board_step ASC
	LIMIT #{endIndex} OFFSET #{startIndex}

SELECT * FROM qna q
LEFT JOIN qna_files qf USING (board_no)
WHERE board_no = #{boardNo}

mapper.update:
UPDATE qna
SET board_title = #{boardTitle}, board_content = #{boardContent}, board_writer = #{boardWriter}

	board_no = #{boardNo};


UPDATE qna
SET board_ref = #{boardNo}
WHERE board_no = #{boardNo}

UPDATE qna
SET board_step = board_step + 1
WHERE board_ref = #{boardRef} AND board_step > #{boardStep}

UPDATE qna
SET board_title = #{boardTitle}
WHERE board_no = #{boardNo}
